import axios from "axios";
import { Modal } from "bootstrap";
import React, { useEffect, useState } from "react";

function AccountEntitlement() {
  const intialData = {
    userName: "",
    password: "",
    role: "",
    name: "",
  };
  const [values, setValue] = useState(intialData);
  const [data, setData] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const handleChange = (name, e) => {
    e.preventDefault();

    setValue((prevalue) => {
      return {
        ...prevalue,
        [name]: e.target.value,
      };
    });
  };
  useEffect(() => {
    getDetails();
  }, []);
  const getDetails = () => {
    axios.get(`http://localhost:3333/getAllUserDetails`).then((res) => {
      console.log("res: ", res);
      console.log(res.data);
      if (res.status === 200) setData(res.data);
    });
  };

  return (
    <div>
      <h2>Account Entitlement</h2>
      
      {showModal ? (
        <>
          <div className="container">
            <div className="row">
              <div className="col"></div>
              <div className="col">
                //----------------------
                <div className="form-outline mb-4">
                  <input
                    type="text"
                    id="name"
                    className="form-control"
                    value={values.name}
                  />
                  <label className="form-label" htmlFor="loginName">
                    Name:
                  </label>
                </div>
                <div className="form-outline mb-4">
                  <input
                    type="text"
                    id="username"
                    className="form-control"
                    value={values.userName}
                  />
                  <label className="form-label" htmlFor="loginName">
                    Username
                  </label>
                </div>
                <div className="form-outline mb-4">
                  <input
                    type="text"
                    id="role"
                    className="form-control"
                    value={values.role}
                  />
                  <label className="form-label" htmlFor="loginName">
                    Role
                  </label>
                </div>
                <br></br>
                <button
                  type="submit"
                  className="btn btn-primary btn-block mb-4"
                  onClick={(e) => {}}
                >
                  Approve
                </button>
                &nbsp; &nbsp;&nbsp;
                <button
                  type="submit"
                  className="btn btn-danger btn-block mb-4"
                  onClick={() => {}}
                >
                  Reject
                </button>
              </div>
              <div className="col"></div>
            </div>

            <dialog id="modal" class="modal">
              <button id="closeModal" class="modal-close-btn">
                Close
              </button>
              <p>nana</p>
            </dialog>
          </div>
        </>
      ) : (
        <>
        
        </>
      )}
    </div>
  );
}

export default AccountEntitlement;
